// App.js
import React, { useState } from 'react';
import Header from './components/Header';
import QueueSection from './components/QueueSection';
import SearchSection from './components/SearchSection';
import WheelSection from './components/WheelSection';
import PlayerProfile from './components/PlayerProfile';
import { QueueProvider } from './contexts/QueueContext';
import './App.css'; // Ensure this import is present

function App() {
  const [activeTab, setActiveTab] = useState('main'); // main or stats

  return (
    <QueueProvider>
      <div className="App">
        <Header />

        {/* Simple tab navigation */}
        <div className="tab-bar" style={{ marginBottom: '20px', textAlign: 'center' }}>
          <button onClick={() => setActiveTab('main')}>Main</button>
          <button onClick={() => setActiveTab('stats')}>Stats</button>
        </div>

        {activeTab === 'main' && (
          <div className="container">
            <div className="grid-container">
              <div className="grid-item">
                <QueueSection />
              </div>
              <div className="grid-item">
                <SearchSection />
              </div>
              <div className="grid-item">
                <WheelSection />
              </div>
              <div className="grid-item">
                <PlayerProfile />
              </div>
            </div>
          </div>
        )}

        {activeTab === 'stats' && (
          <div className="container">
            {/* Another 4-item grid for the Stats tab */}
            <div className="grid-container">
              <div className="grid-item">Stats Item 1</div>
              <div className="grid-item">Stats Item 2</div>
              <div className="grid-item">Stats Item 3</div>
              <div className="grid-item">Stats Item 4</div>
            </div>
          </div>
        )}
      </div>
    </QueueProvider>
  );
}

export default App;
