import React, { useState } from 'react';
import axios from 'axios';

const SearchSection = () => {
  const [summonerName, setSummonerName] = useState('');
  const [summonerTagline, setSummonerTagline] = useState('');
  const [error, setError] = useState(null);
  const [scores, setScores] = useState([]);
  const [playerToRemove, setPlayerToRemove] = useState(null);
  const [newPlayerAdded, setNewPlayerAdded] = useState(null);

  // Function to fetch the latest queue
  const fetchQueue = async () => {
    try {
      const response = await axios.get('/api/queue');
      // Assuming you have a context or a higher-level state to manage the queue
      // Alternatively, you can pass a prop to update the queue in a parent component
      // For simplicity, we'll log it here
      console.log('Updated Queue:', response.data.queue);
      // TODO: Update the queue state in the relevant component
    } catch (error) {
      console.error('Error fetching queue:', error);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setError(null);
    setScores([]);
    setPlayerToRemove(null);
    setNewPlayerAdded(null);

    try {
      const response = await axios.get('/api/search', {
        params: {
          summoner_name: summonerName,
          summoner_tagline: summonerTagline,
        },
      });

      const data = response.data;
      setScores(data.scores);
      setPlayerToRemove(data.player_to_remove);
      setNewPlayerAdded(data.new_player_added);

      // After updating scores and player removal, fetch the latest queue
      fetchQueue();
    } catch (error) {
      console.error('Error searching player:', error);
      if (error.response && error.response.data && error.response.data.error) {
        setError(error.response.data.error);
      } else {
        setError('An unexpected error occurred.');
      }
    }
  };

  return (
    <div className="search">
      <h2>Search for a Player</h2>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={summonerName}
          onChange={(e) => setSummonerName(e.target.value)}
          placeholder="Enter Summoner Name"
          required
        />
        <input
          type="text"
          value={summonerTagline}
          onChange={(e) => setSummonerTagline(e.target.value)}
          placeholder="Enter Summoner Tagline"
          required
        />
        <button type="submit">Search</button>
      </form>

      {/* Display Errors */}
      {error && <p className="error">{error}</p>}

      {/* Display Scores */}
      {scores.length > 0 && (
        <>
          <h2>Team Members' Scores</h2>
          <ul>
            {scores.map((score, index) => (
              <li key={index}>
                <strong>{score.summonerName}</strong>: {score.score}
              </li>
            ))}
          </ul>
        </>
      )}

      {/* Display Player to Remove */}
      {playerToRemove && (
        <h3>
          Player to Remove: {playerToRemove.summonerName} with a score of {playerToRemove.score}
        </h3>
      )}

      {/* Display New Player */}
      {newPlayerAdded && <h3>New Player Added: {newPlayerAdded}</h3>}
    </div>
  );
};

export default SearchSection;
