import React, { useContext } from 'react';
import { QueueContext } from '../contexts/QueueContext';

const QueueSection = () => {
  const { queue, loading, error } = useContext(QueueContext);

  if (loading) return <p>Loading queue...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="queue">
      <h2>Queue</h2>
      <h3>Current Queue</h3>
      <ul>
        {queue.length > 0 ? (
          queue.map((player, index) => <li key={index}>{player}</li>)
        ) : (
          <li>No players in the queue.</li>
        )}
      </ul>
    </div>
  );
};

export default QueueSection;
