// components/GraphSection.js
import React, { useState, useEffect } from "react";
import LineChart from "./LineChart";

const GraphSection = () => {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    fetch("https://api.blackultras.com/api/scores")
      .then((response) => response.json())
      .then((data) => {
        // Process the API response to prepare data for the chart
        const playerScores = data.player_scores; // Assuming this is the API's response format

        const labels = []; // To store unique match dates
        const datasets = []; // To store player-specific score datasets

        Object.entries(playerScores).forEach(([playerName, games]) => {
          // Extract dates and scores for the player
          games.forEach((game) => {
            if (!labels.includes(game.match_date)) {
              labels.push(game.match_date);
            }
          });

          // Create a dataset for the player
          datasets.push({
            label: playerName, // Player's name
            data: games.map((game) => game.score), // Player's scores
            borderColor: `rgba(${Math.floor(Math.random() * 255)}, ${Math.floor(Math.random() * 255)}, ${Math.floor(Math.random() * 255)}, 1)`,
            backgroundColor: "rgba(0, 0, 0, 0)", // Transparent
            borderWidth: 2
          });
        });

        // Prepare the chart data
        const chartData = {
          labels, // X-axis labels (match dates)
          datasets // Y-axis datasets (scores for each player)
        };

        setChartData(chartData);
      })
      .catch((err) => console.error("Failed to fetch scores:", err));
  }, []);

  if (!chartData) return <div>Loading chart...</div>;

  return (
    <div className="graph-section">
      <LineChart chartData={chartData} />
    </div>
  );
};

export default GraphSection;
