// src/components/QueueSection.js
import React, { useState, useEffect } from 'react';
import axios from 'axios'; // If using axios

const QueueSection = () => {
  const [queue, setQueue] = useState([]);
  const [playerName, setPlayerName] = useState('');
  const [message, setMessage] = useState('');

  // Fetch the current queue from the backend
  useEffect(() => {
    fetchQueue();
  }, []);

  const fetchQueue = async () => {
    try {
      const response = await axios.get('/api/queue');
      setQueue(response.data.queue);
    } catch (error) {
      console.error('Error fetching queue:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!playerName.trim()) return;

    try {
      const response = await axios.post('/api/queue', { player_name: playerName });
      setMessage(response.data.message);
      setQueue(response.data.queue);
      setPlayerName('');
    } catch (error) {
      console.error('Error adding to queue:', error);
      setMessage('Failed to add to queue.');
    }
  };

  return (
    <div className="queue">
      <h2>Queue</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          placeholder="Enter Your Name"
          required
        />
        <button type="submit">Join Queue</button>
      </form>
      {message && <p>{message}</p>}
      <h3>Current Queue</h3>
      <ul>
        {queue.length > 0 ? (
          queue.map((player, index) => <li key={index}>{player}</li>)
        ) : (
          <li>No players in the queue.</li>
        )}
      </ul>
    </div>
  );
};

export default QueueSection;
